sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }


   var targetFieldName = "u_what_needed";

	g_form.clearOptions(targetFieldName);

   if(newValue === "hr"){
	g_form.addOption(targetFieldName, "hr1", "HR 1");
	g_form.addOption(targetFieldName, "hr2", "HR 2");
	g_form.addOption(targetFieldName, "other", "Other");
   }

   else if(newValue === "facilities"){
	g_form.addOption(targetFieldName, "facilities1", "Facilities 1");
	g_form.addOption(targetFieldName, "facilities1", "Facilities 2");
	g_form.addOption(targetFieldName, "other", "Other");
   }

   else if(newValue === "legal"){
	g_form.addOption(targetFieldName, "legal1", "Legal 1");
	g_form.addOption(targetFieldName, "legal1", "Legal 2");
	g_form.addOption(targetFieldName, "other", "Other");
   }

   if(isLoading && !g_form.isNewRecord()){
		g_form.setValue(targetFieldName, g_form.getValue(targetFieldName));
	}

}
